INSERT INTO `applications` VALUES ('55', 'com.Gnathonic.SystemStatsLivePro', 'System Stats Live Pro', '109', '0.5.9.1', '3', 'Now includes an app in the launcher, for those who like System Stats, just not as a wallpaper<p>Lets you use WiFi, Cellular, CPU, RAM usage stats and a (or several) logcat as your live wallpaper<p>Or use the same stats in a widget on your homescreen<p>Vote for and suggest new features in app<p>App also adds easy access to the editor<p>Highly customizable<p>Wallpaper supports multiple layouts and lets you switch between them by double tapping<p>Scrollable logs that can be halted for easy reading<p>13 fonts to choose from<p>Efficient data collection<p>Can report in internals faster than any other perpetual resource tracking app on the market (up to 8 updates per second)<p>Can aggregate and report on over 6 months worth of data<p>Built-in layout editor so you can customize your layouts on the go.<p>Coming Soon: Dynamic Labels, Layout Sharing (import/export/send), Battery Stats, More Graph Options, Website, and some Documentation.<p>Don&#39;t see the feature you want in that list? Ask for it. With the exception of Dynamic Labels, all of the soon to be introduced features were suggested by users like yourself.<br>Got any other comments or complaints? Send me an Email.</p></p></p></p></p></p></p></p></p></p></p></p></p></p>', '7', '287043', '0', '0', '0', '2015-03-20 18:36:49', '2015-03-20 18:36:49', '0', '1', '0', '0', 'Gnathonic', null);
